import React, { useEffect, useState } from 'react';
import { Grid, Typography, Button, Box } from '@mui/material';
import Layout from './Layout';
import InputFormDialog from '../components/InputFormDialog';
import Axios from '../../axios';
import useAuth from '../useAuth';

const getBoxStyle = (filled, total) => {
  if (filled === total) {
    return {
      backgroundColor: 'red',
      color: 'white',
    };
  } else if (filled === 0) {
    return {
      backgroundColor: '#90e0ef',
      color: 'black',
    };
  } else {
    return {
      backgroundColor: '#06d6a0',
      color: 'white',
    };
  }
};

const Home = () => {
  const { name } = useAuth();
  const total = 7;
  const [dialog, setDialog] = useState(false);
  const [selectedBox, setSelectedBox] = useState(null);
  const [partNumber, setPartNumber] = useState(null);
  const [bookingData, setBookingData] = useState([]);

  const getData = async () => {
    try {
      const result = await Axios.get('/cows');
      if (result.status === 200) {
        setBookingData(result.data);
      }
    } catch (e) {
      console.log('err->', e);
    }
  };

  useEffect(() => {
    getData();
  }, []);

  const refreshData = () => {
    getData();
  };

  const handleCreateNew = () => {
    const lastBox = bookingData[bookingData.length - 1];
    if (lastBox.AllocatedParts === total) {
      setBookingData([
        ...bookingData,
        { ID: bookingData.length + 1, TotalParts: total, AllocatedParts: 0 },
      ]);
    }
  };

  const handleBoxClick = (ID, AllocatedParts) => {
    if (AllocatedParts < total) {
      setPartNumber(AllocatedParts);
      setSelectedBox(ID);
      setDialog(true);
    }
  };

  const lastBox = bookingData[bookingData.length - 1];

  console.log("lastBox-> ", lastBox)
  return (
    <Layout>
      <InputFormDialog
        open={dialog}
        setOpen={() => setDialog(!dialog)}
        selectedAnimal={selectedBox}
        partNumber={partNumber + 1}
        refreshData={refreshData}
        bookerName={name}
      />
      <Box sx={{pt:5, pl:2}}>
      <Button onClick={handleCreateNew} disabled={!lastBox || lastBox.AllocatedParts < total}>
        Create New
      </Button>
      </Box>
      <Grid container pt={5} pl={2}>
        {lastBox && (
          <Grid
            item
            xs={6}
            sm={5}
            md={3}
            lg={2.2}
            onClick={() => handleBoxClick(lastBox.ID, lastBox.AllocatedParts)}
            sx={{
              position: 'relative',
              padding: 2,
              m: 1,
              textAlign: 'center',
              borderRadius: 2,
              boxShadow: 3,
              ...getBoxStyle(lastBox.AllocatedParts, total),
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'center',
              minHeight: '180px',
              cursor: lastBox.AllocatedParts === total ? 'default' : 'pointer',
              '&:hover': {
                opacity: lastBox.AllocatedParts === total ? 1 : 0.8,
              },
            }}
          >
            <Typography
              variant="h6"
              sx={{
                position: 'absolute',
                top: 8,
                left: 8,
                fontSize: '1rem',
                backgroundColor: 'rgba(255, 255, 255, 0.7)',
                borderRadius: '4px',
                padding: '2px 4px',
              }}
            >
              {bookingData.length}
            </Typography>
            <Typography variant="h6">
              {lastBox.AllocatedParts === total ? 'Completed' : `${lastBox.AllocatedParts}/${total}`}
            </Typography>
          </Grid>
        )}
      </Grid>
    </Layout>
  );
};

export default Home;
